import React from "react";
import { Container, Row, Col, Card, CardHeader, CardBody, Form, Button } from "shards-react";
import { Dropdown, DropdownMenu, DropdownToggle, DropdownItem } from 'react-bootstrap';
// import ScriptTag from 'react-script-tag';
import * as d3 from "d3";
import Tree from 'react-tree-graph';
 




class Tables extends React.Component {
    constructor(props) {
    super(props);
    this.state={
    fabrics : ["Q1", "Q2"],
    tree_data : {
        name: 'Parent',
        children: [{
            name: 'Child One'
        }, {
            name: 'Child Two'
        }]
    }
}
    }


    plotGraph = () => {

    }

    render(){
        // const { tree_data } = this.tree_data;
        const  { fabrics } = this.fabrics;
        return(
            <Container fluid className="main-content-container px-4">


            <Form action="http://192.168.8.105:5000/bubblechart" target="_blank">
            {/* <input type="submit" value="Track Last Hour Movements" /> */}
            <Button>Track Last Hour Movements</Button>
            </Form>

            <div>
            <Tree
            data={this.state.tree_data}
            height={400}
            width={400}/>
            </div>

            <div className="card mb-4">
                <div className="card-body">
                    <div className="card-header">
                   
                        
                    {/* <button onClick={this.buttonClick.bind(this)}>Click me</button>   {function(evt){console.log(evt)}} */}
                            <Dropdown className="d-inline-block mr-2 mt-2 card-title" onSelect={this.componentDidMount}>
                                        <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                                            {this.state.cropdown_title_class}
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                        {/* onClick={this.buttonClick("try")}   onClick={this.buttonClick(object.name)} */}
                                        {Array.isArray(fabrics) && fabrics.map(object => (
                                        <Dropdown.Item eventKey={object.f_id}>{object.name}</Dropdown.Item>
                                        // <Dropdown.MenuItem key={object.name} href={'#${object.name}'}></Dropdown.MenuItem>
                                        ))}
                                        {/* <Dropdown.MenuItem onClick={this.buttonClick(value)}></Dropdown.MenuItem> */}
                                            
                                            {/* <Dropdown.Item href="#/action-3">Slubnnnnn</Dropdown.Item> */}
                                            
                                        </Dropdown.Menu>
                                    </Dropdown>
                            
                       

                       
                   
                        
                        
                    </div>
                    
                </div>
            </div>

            </Container>
        )
    }
} 

export default Tables;
